DECLARE @tbl TABLE
(
	 EntryId INT IDENTITY(0, 1) NOT NULL
	,SomeValue INT
);

DECLARE @i INT = 0;
DECLARE @count INT = (SELECT COUNT(EntryId) FROM @tbl);

WHILE @i < @count
BEGIN
	DECLARE @someValue INT;

	SELECT @someValue = SomeValue FROM @tbl WHERE EntryId = @i;

	-- Do stuff here

	SET @i += 1;
END
